{
    "sections": [
        {
            "title": "Movement origin",
            "sectionKey": "origin",
            "questionAnswers": [
                {
                    "question": "Are you moving the animals on or off your farm or premises?",
                    "questionKey": "onOffFarm",
                    "answer": {
                        "type": "radio",
                        "value": "off",
                        "displayText": "Off the farm or premises"
                    }
                },
                {
                    "question": "What type of premises are the animals moving off?",
                    "questionKey": "originType",
                    "answer": {
                        "type": "radio",
                        "value": "tb-restricted-farm",
                        "displayText": "TB restricted farm"
                    }
                },
                {
                    "question": "What is the County Parish Holding (CPH) number of your farm or premises where the animals are moving off?",
                    "questionKey": "cphNumber",
                    "answer": {
                        "type": "text",
                        "value": "12/345/6789",
                        "displayText": "12/345/6789"
                    }
                },
                {
                    "question": "What is the address of your farm or premises where the animals are moving off?",
                    "questionKey": "address",
                    "answer": {
                        "type": "address",
                        "value": {
                            "addressLine1": "Starfleet Headquarters",
                            "addressTown": "San Francisco",
                            "addressPostcode": "N11 1NN"
                        },
                        "displayText": "Starfleet Headquarters\nSan Franciso\nN11 1NN"
                    }
                }
            ]
        },
        {
            "title": "Movement destination",
            "sectionKey": "destination",
            "questionAnswers": [
                {
                    "question": "Where are the animals going to?",
                    "questionKey": "destinationType",
                    "answer": {
                        "type": "radio",
                        "value": "slaughter",
                        "displayText": "Slaughter"
                    }
                }
            ]
        },
        {
            "title": "Receiving the licence",
            "sectionKey": "licence",
            "questionAnswers": [
                {
                    "question": "What is the name of the registered owner of the cattle?",
                    "questionKey": "fullName",
                    "answer": {
                        "type": "name",
                        "value": {
                            "firstName": "John",
                            "lastName": "Doe"
                        },
                        "displayText": "John Doe"
                    }
                },
                {
                    "question": "How would you like this licence sent to you?",
                    "questionKey": "receiveMethod",
                    "answer": {
                        "type": "radio",
                        "value": "email",
                        "displayText": "Email"
                    }
                },
                {
                    "question": "What email address would you like the licence sent to?",
                    "questionKey": "emailAddress",
                    "answer": {
                        "type": "text",
                        "value": "name@example.com",
                        "displayText": "eoin.corr@esynergy.co.uk"
                    }
                }
            ]
        }
    ]
}

